import {StyleSheet, Alert, TouchableOpacity, Platform, Animated, Easing} from 'react-native';
import {Text, View} from '../components/Themed';
import MapView, {AnimatedRegion, Camera, Marker, PROVIDER_GOOGLE} from 'react-native-maps';
import {RootTabScreenProps} from '../types';
import React, {useState, useRef, useEffect, useCallback, RefObject} from "react";
import * as Location from 'expo-location';
import {LocationAccuracy, LocationObject} from 'expo-location';
import {FontAwesome} from '@expo/vector-icons';

export default function MapScreen({navigation}: RootTabScreenProps<'MapTab'>) {
    const map = useRef();

    let playerMarker: RefObject<Marker> = null;

    const playerMarkerCallback = useCallback(async (marker) => {
        playerMarker = marker;
        await updateLocation();
    }, []);

    const [loading, setLoading] = useState(false);
    const [follow, setFollow] = useState(false);

    const [playerLocation, setPlayerLocation] = useState(null);
    const [playerHeading, setPlayerHeading] = useState(0);
    const [cameraHeading, setCameraHeading] = useState(0);

    const [cameraRegion, setCameraRegion] = useState({
        latitude: 37.78825,
        longitude: -122.4324,
        latitudeDelta: 0.0922,
        longitudeDelta: 0.0421,
    });

    const [playerRegion, setPlayerRegion] = useState({
        latitude: 37.78825,
        longitude: -122.4324,
        latitudeDelta: 0.0922,
        longitudeDelta: 0.0421,
    });

    let playerMarkerCoords = new AnimatedRegion(playerRegion);
    const playerMarkerRot = useState(new Animated.Value(0))[0];

    const locToCoords = (loc: LocationObject) => {
        return {latitude: loc.coords.latitude, longitude: loc.coords.longitude}
    }

    const onMapReady = () => {
        setLoading(false);
    };

    const updateCameraHeading = () => {
        // map.current.getCamera().then((info: Camera) => {
        //     setCameraHeading(info.heading);
        // });
    }

    useEffect(() => {
        console.log("playerHeading");
        Animated.timing(playerMarkerRot, {
            toValue: (playerHeading - cameraHeading),
            duration: 300,
            easing: Easing.linear,
            useNativeDriver: false,
        }).start();
        // if (follow) {
        //     map.current.animateCamera({
        //         heading: newHeading,
        //     }, {duration: 500});
        //
        // }

    }, [playerHeading, cameraHeading]);

    useEffect(() => {
        if (playerLocation == null) return;
        const newCoords = locToCoords(playerLocation);

        setPlayerRegion({
            ...playerRegion,
            ...newCoords
        });

        animateMarker(playerMarker, newCoords);

    }, [playerLocation]);

    useEffect(() => {
        (async () => {
            // Location.watchPositionAsync({accuracy: LocationAccuracy.Highest}, loc => setPlayerLocation(loc));
            // Location.watchHeadingAsync(heading => setPlayerHeading(heading.trueHeading));
        });
    }, []);

    const animateMarker = (marker, newCoords) => {
        if (Platform.OS === 'android') {
            if (marker != null) {
                console.log(newCoords);
                marker.animateMarkerToCoordinate(newCoords, 500);
            }
        } else {
            // `useNativeDriver` defaults to false if not passed explicitly
            playerMarkerCoords.timing({...newCoords, useNativeDriver: true}).start();
        }
    }

    const animateCamera = async (newCoords) => {
        setFollow(false);
        const camera: Camera = map.current.getCamera();

        map.current.animateCamera({
            center: newCoords,
            zoom: 15
        }, {duration: 500});
    }

    const updateLocation = async () => {
        let {status} = await Location.requestForegroundPermissionsAsync();
        if (status !== 'granted') {
            Alert.alert('Permission to access location was denied');
            return null;
        }

        const loc = await Location.getCurrentPositionAsync({});
        setPlayerLocation(loc);
    };

    const followMe = async () => {
        setFollow(current => !current);
    };

    const findMe = async () => {
        await updateLocation();
        console.log(playerLocation);
        animateCamera(locToCoords(playerLocation));
    };

    const onCameraRegionChange = (newRegion) => {
        setCameraRegion(newRegion);
    }

    return (
        <View style={styles.container} >
            {loading
                ? <Text>Loading...</Text>
                : <>
                    <MapView
                        ref={map}
                        provider={PROVIDER_GOOGLE}
                        {...(follow && {region: playerRegion})}
                        scrollEnabled={!follow}
                        onRegionChange={onCameraRegionChange}
                        onMapReady={onMapReady}
                        customMapStyle={mapStyle}
                        style={styles.map}
                        onTouchEnd={() => {
                            updateCameraHeading();
                        }}
                        onTouchCancel={() => {
                            updateCameraHeading();
                        }}
                        onTouchStart={() => {
                            updateCameraHeading();
                        }}
                        onTouchMove={() => {
                            updateCameraHeading();
                        }}
                    >
                        <Marker.Animated
                            anchor={{x: 0.5, y: 0.5}}
                            rotation={playerMarkerRot}
                            ref={playerMarkerCallback}
                            coordinate={playerMarkerCoords}
                        >
                            <FontAwesome size={30} name="location-arrow" />
                        </Marker.Animated>
                    </MapView>
                    <View style={[styles.buttonContainer, styles.topRight]}>
                        <TouchableOpacity
                            style={[styles.bubble, styles.button]}
                        >
                            <Text>{playerRegion.latitude.toFixed(3)}, {playerRegion.longitude.toFixed(3)}</Text>
                        </TouchableOpacity>
                    </View>
                    <View style={[styles.buttonContainer, styles.bottomRight]}>
                        <TouchableOpacity
                            onPress={() => followMe()}
                            style={[styles.bubble, styles.button, follow ? styles.active : {}]}
                        >
                            <FontAwesome color={follow ? "white" : "black"} size={30} name="compass" />
                        </TouchableOpacity>
                        <TouchableOpacity
                            onPress={() => findMe()}
                            style={[styles.bubble, styles.button]}
                        >
                            <FontAwesome size={30} name="crosshairs" />
                        </TouchableOpacity>
                    </View>
                </>}
        </View >
    );
}

const styles = StyleSheet.create({
    container: {
        ...StyleSheet.absoluteFillObject,
        justifyContent: 'center',
        alignItems: 'center',
    },
    loading: {
        display: 'flex'
    },
    title: {
        fontSize: 20,
        fontWeight: 'bold',
    },
    separator: {
        marginVertical: 30,
        height: 1,
        width: '80%',
    },
    map: {
        ...StyleSheet.absoluteFillObject,
    },
    button: {
        paddingHorizontal: 12,
    },
    active: {
        backgroundColor: 'rgba(0,0,0,0.4)',
    },
    bubble: {
        backgroundColor: 'rgba(255,255,255,0.7)',
        paddingHorizontal: 18,
        paddingVertical: 12,
        borderRadius: 20,
        marginBottom: 10,
    },
    buttonContainer: {
        position: 'absolute',
        marginHorizontal: 10,
        backgroundColor: 'transparent',
    },
    bottomRight: {
        right: 0,
        bottom: 0,
    },
    topRight: {
        right: 0,
        top: 0,
    }
});

